import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const CreateButtonComponent = ({ data, columns, apiurl }) => {
    const [editedData, setEditedData] = useState({ ...data }); // Use spread operator to create a copy of the data
    const [isEditing, setIsEditing] = useState(false);

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseInt(value, 10) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            const response = await axios.post(`${apiurl}`, editedData); // Use PUT request to update existing data

            if (response.status === 200) {
                toast.success("Item modified successfully!");
                setIsEditing(false);
            } else {
                toast.error("Error modifying item");
            }
        } catch (error) {
            toast.error("Error modifying item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    return (
        <>
            <button onClick={handleEditClick}>Edit</button>{" "}
            {/* Change button label to "Edit" */}
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {Object.keys(data).map((key) => (
                            <div key={key}>
                                <label htmlFor={key}>{key}</label>
                                <input
                                    type="text"
                                    id={key}
                                    name={key}
                                    value={editedData[key]}
                                    onChange={handleInputChange}
                                />
                            </div>
                        ))}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default CreateButtonComponent;
