import React, { useState, useEffect } from "react";
import axios from "axios";
import "./table.css";
import DeleteButtonComponent from "../components/DeleteButton";

const TableInfoComponent = ({ url, keysToDisplay }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(url);
                const newData = response.data.map((item) =>
                    keysToDisplay.map((key) => item[key])
                );
                setData(newData);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, [url, keysToDisplay]);
    console.log(data);
    return (
        <>
            {data.map((row, rowIndex) => (
                <tr key={rowIndex} className="infoRow">
                    {row.map((cell, cellIndex) => (
                        <td key={cellIndex}>{cell}</td>
                    ))}
                    <td>
                        <DeleteButtonComponent
                            url="http://localhost:8080/advertisement/monitor"
                            id={data.id}
                        />
                    </td>
                </tr>
            ))}
        </>
    );
};

export default TableInfoComponent;
