import React, { useState, useEffect } from "react";
import "./Table.css";
import DeleteButtonComponent from "./DeleteButton";
import EditButtonComponent from "./EditButton";
import Modal from "./Modal";
const DataTableComponent = ({ data, columns, apiUrl, onDataFetched }) => {
    const [fetchedData, setFetchedData] = useState([]);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(10);

    useEffect(() => {
        setFetchedData(data);
    }, [data]);

    const handleDataFetched = (updatedData) => {
        setFetchedData(updatedData);
        onDataFetched(updatedData);
    };

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = fetchedData.slice(indexOfFirstItem, indexOfLastItem);

    // Logic to change page
    const paginate = (pageNumber) => setCurrentPage(pageNumber);
    return (
        <div>
            <table className="dataTable">
                <thead className="tableHeader">
                    <tr>
                        {columns.map((column, index) => (
                            <th key={index}>{column.header}</th>
                        ))}
                        <th>delete</th>
                        <th>Edit</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, rowIndex) => (
                        <tr className="infoRow" key={rowIndex}>
                            {columns.map((column, columnIndex) => (
                                <td className="infoRow" key={columnIndex}>
                                    {row[column.key]}
                                </td>
                            ))}
                            <td>
                                <DeleteButtonComponent
                                    id={row.id}
                                    apiUrl={apiUrl}
                                    column={columns}
                                    dataFetching={handleDataFetched}
                                />
                            </td>
                            <td>
                                <EditButtonComponent
                                    data={row}
                                    columns={columns}
                                    apiurl={apiUrl}
                                    id={row.id}
                                    onSave={handleDataFetched}
                                    onEditClick={() => setIsModalVisible(true)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                {Array.from(
                    { length: Math.ceil(fetchedData.length / itemsPerPage) },
                    (_, index) => (
                        <button
                            key={index + 1}
                            onClick={() => paginate(index + 1)}
                        >
                            {index + 1}
                        </button>
                    )
                )}
            </div>
        </div>
    );
};

export default DataTableComponent;
