import React, { useState } from "react";
import axios from "axios";

const FormDataForm = ({ apiUrl, onDataUpdate, itemKeys }) => {
    const [formData, setFormData] = useState({});

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(apiUrl, formData);
            onDataUpdate();
        } catch (error) {
            console.error("Error submitting form data:", error);
        }
    };

    const handleInputChange = (e, key, dataType) => {
        let value = e.target.value;

        if (dataType === "integer") {
            value = parseInt(value, 10);
        } else if (dataType === "boolean") {
            value = e.target.checked;
        } else if (dataType === "date") {
            const regex = /^\d{4}-\d{2}-\d{2}$/;
            if (!regex.test(value)) {
                console.error("Invalid date format");
                return;
            }
        }

        setFormData({ ...formData, [key]: value });
    };

    return (
        <form onSubmit={handleSubmit}>
            {Object.keys(itemKeys).map((key) => (
                <div key={key}>
                    <label htmlFor={key}>{key}</label>
                    {itemKeys[key] === "boolean" ? (
                        <input
                            type="checkbox"
                            id={key}
                            checked={formData[key] || false}
                            onChange={(e) =>
                                handleInputChange(e, key, "boolean")
                            }
                        />
                    ) : itemKeys[key] === "date" ? (
                        <input
                            type="date"
                            id={key}
                            value={formData[key] || ""}
                            onChange={(e) => handleInputChange(e, key, "date")}
                        />
                    ) : itemKeys[key] === "email" ? (
                        <input
                            type="email"
                            id={key}
                            value={formData[key] || ""}
                            onChange={(e) => handleInputChange(e, key, "email")}
                        />
                    ) : (
                        <input
                            type={
                                itemKeys[key] === "integer" ? "number" : "text"
                            }
                            id={key}
                            value={formData[key] || ""}
                            onChange={(e) =>
                                handleInputChange(e, key, itemKeys[key])
                            }
                        />
                    )}
                </div>
            ))}
            <button type="submit">Submit</button>
        </form>
    );
};

export default FormDataForm;
