import React, { useState } from "react";
import axios from "axios";

const FormDataForm = ({ apiUrl, formFields }) => {
    const initialFormData = Object.fromEntries(
        formFields.map((field) => [field.name, ""])
    );

    const [formData, setFormData] = useState(initialFormData);
    const [dataUpdated, setDataUpdated] = useState(false);

    const handleDataUpdate = () => {
        setDataUpdated(!dataUpdated);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(apiUrl, formData);
            handleDataUpdate();
        } catch (error) {
            console.error("Error submitting form data:", error);
        }
    };

    const handleInputChange = (e, fieldName, dataType) => {
        let value = e.target.value;

        if (dataType === "integer") {
            value = parseInt(value, 10);
        } else if (dataType === "boolean") {
            value = e.target.checked;
        } else if (dataType === "date") {
            const regex = /^\d{4}-\d{2}-\d{2}$/;
            if (!regex.test(value)) {
                console.error("Invalid date format");
                return;
            }
        }

        setFormData({ ...formData, [fieldName]: value });
    };

    return (
        <form style={{ color: "white" }} onSubmit={handleSubmit}>
            {formFields.map((field) => (
                <div key={field.name}>
                    <label htmlFor={field.name}>{field.label}</label>
                    {field.type === "checkbox" ? (
                        <input
                            type={field.type}
                            id={field.name}
                            checked={formData[field.name] || false}
                            onChange={(e) =>
                                handleInputChange(e, field.name, "boolean")
                            }
                        />
                    ) : field.type === "date" ? (
                        <input
                            type={field.type}
                            id={field.name}
                            value={formData[field.name] || ""}
                            onChange={(e) =>
                                handleInputChange(e, field.name, "date")
                            }
                        />
                    ) : (
                        <input
                            type={field.type}
                            id={field.name}
                            value={formData[field.name] || ""}
                            onChange={(e) =>
                                handleInputChange(e, field.name, "text")
                            }
                        />
                    )}
                </div>
            ))}
            <button type="submit">Submit</button>
        </form>
    );
};

export default FormDataForm;
