import "./advertisement.css"
import React, { useState } from "react";
import axios from "axios";
import { useCookies } from 'react-cookie';
import jwtDecode from 'jwt-decode';
import ApplicationForm from "./applicationForm";

function Advertisement(props) {
    const { title, location, name, postdate, advertisementid, companyid } = props.advertisement;
    const [moreInfo, setMoreInfo] = useState(undefined);
    const [showForm, setShowForm] = useState(false);
    const [cookies] = useCookies(['jwt']);
    const jwt = cookies.jwt ? jwtDecode(cookies.jwt) : undefined;

    const HandleSeeMore = () => {
        axios.get(`http://localhost:8080/advertisement/${advertisementid}`).then(res => {
            setMoreInfo(res.data);
        }).catch((error) => {
            console.log(error);
        });
    };

    const HandleApply = () => {
        if (jwt?.userId) {
            axios.post("http://localhost:8080/application",
                {
                    advertisementid: advertisementid,
                    companyid: companyid,
                    email: jwt.email,
                    firstname: jwt.firstName,
                    lastname: jwt.lastName
                })
                .then((response) => {
                    console.log(response);
                })
                .catch((error) => {
                    console.log(error);
                });
        } else {
            setShowForm(true);
        }
    };

    return (
        <div className="advertisement" key={advertisementid}>
            <p>{title}</p>
            <p>Place: {location}</p>
            <p>Work at: {name}</p>
            <p>Advertisement created at {postdate}</p>
            {moreInfo &&
                <>
                    <p>{moreInfo.description}</p>
                    <p>{moreInfo.wage}</p>
                    <p>{moreInfo.contact}</p>
                </>
            }
            <button className="primary-button" onClick={HandleSeeMore}>See more</button>
            <button className="primary-button" onClick={HandleApply}>I apply</button>
            {showForm &&
                    <ApplicationForm advertisementid={advertisementid} companyid={companyid} />
            }
        </div>
    );
}

export default Advertisement;
