import React, { useState } from "react";
import GetAllData from "./getanddelete";
import FormDataForm from "./generalform";

const Applications = () => {
    const [dataUpdated, setDataUpdated] = useState(false);

    const handleDataUpdate = () => {
        setDataUpdated(!dataUpdated);
    };
    return (
        <div>
            <div>
                {" "}
                <GetAllData
                    apiUrl="http://localhost:8080/application"
                    itemName="Applications"
                    onDataUpdate={handleDataUpdate}
                    itemKeys={[
                        "id",
                        "firstname",
                        "lastname",
                        "email",
                        "applicationdate",
                    ]}
                />
            </div>
            <div>
                <FormDataForm
                    apiUrl={"http://localhost:8080/application"}
                    onDataUpdate={handleDataUpdate}
                    itemKeys={{
                        firstname: "text",
                        lastname: "text",
                        email: "email",
                        postdate: "date",
                        companyid: "integer",
                        advertisementid: "integer",
                    }}
                />
            </div>
        </div>
    );
};

export default Applications;
