import React, { useState, useEffect } from "react";
import axios from "axios";

const fetchadvertisements = async () => {
    try {
        const response = await axios.get("http://localhost:8080/application");
        return response.data;
    } catch (error) {
        console.error("Error:", error);
        throw error;
    }
};

const advertisements = () => {
    const [advertisements, setadvertisements] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchadvertisements();
                setadvertisements(data);
            } catch (error) {
                // Handle error if needed
                console.error("Error fetching advertisements:", error);
            }
        };

        fetchData();
    }, []); // Empty dependency array means this effect runs once after the initial render

    return (
        <div>
            <h2>Job advertisements</h2>
            {advertisements.map((application) => (
                <div key={application.id}>
                    <h3>
                        {application.name}&nbsp;
                        {application.title}
                    </h3>
                    <p>{application.firstname}</p>
                    <p>{application.lastname}</p>
                    <p>{application.email}</p>
                    <p>{application.title}</p>
                </div>
            ))}
        </div>
    );
};

export default advertisements;
