import React, { useState } from "react";
import DeleteButton from "./deleteButton";

import axios from "axios"; // Don't forget to import axios

const TableRow = ({ item, columns, apiUrl, onDataUpdate }) => {
    const [isEditing, setEditing] = useState(false);
    const [editedData, setEditedData] = useState(item);

    const handleEdit = () => {
        setEditedData(item); // Reset editedData to the original data when entering edit mode
        setEditing(true);
    };

    const handleSave = () => {
        // Call your API endpoint to save editedData here (PUT request)
        // Then setEditing(false) and update the table data using onDataUpdate
        setEditing(false);
        onDataUpdate(); // You might need to fetch updated data from the server.
    };

    const handleCancelEdit = () => {
        setEditing(false);
    };
    return (
        <tr>
            {columns.map((key, index) => (
                <td key={index}>
                    {typeof item[key] === "boolean"
                        ? item[key]
                            ? "Yes"
                            : "No"
                        : item[key]}
                </td>
            ))}
            <td>
                <DeleteButton
                    apiUrl={apiUrl}
                    id={item.id}
                    buttonText={"Delete"}
                    onDataUpdate={onDataUpdate}
                />
            </td>
        </tr>
    );
};

export default TableRow;
