import React, { useState } from "react";
import GetAllData from "./getanddelete";

const Companies = () => {
    const [dataUpdated, setDataUpdated] = useState(false);

    const handleDataUpdate = () => {
        setDataUpdated(!dataUpdated);
    };
    return (
        <div>
            <div>
                <GetAllData
                    apiUrl="http://localhost:8080/companies"
                    itemName="Companies"
                    onDataUpdate={handleDataUpdate}
                    itemKeys={["name"]}
                />
            </div>

            <div>
                <FormDataForm
                    apiUrl={"http://localhost:8080/advertisement/"}
                    onDataUpdate={handleDataUpdate}
                    itemKeys={{
                        companyid: "text",
                        title: "text",
                        description: "text",
                        location: "text",
                        wage: "integer",
                        contact: "email",
                    }}
                />
            </div>
        </div>
    );
};

export default Companies;
