import React, { useState, useEffect } from "react";

import NavBar from "./NavBar";

import axios from "axios";

import DataFetchingComponent from "./DataFetcher";
import DataTableComponent from "./DataTable";
import CreateButtonComponent from "./CreateButton";

import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import jwtDecode from "jwt-decode";

import { toast } from "sonner";

const GenericPage = ({ pageTitle, apiUrl, columns }) => {
    const [fetchedData, setFetchedData] = useState([]);

    const [cookies] = useCookies(["jwt"]);
    const navigate = useNavigate();

    useEffect(() => {
        const jwtToken =
            cookies.jwt !== "undefined" && cookies.jwt !== undefined
                ? jwtDecode(cookies.jwt)
                : undefined;

        if (!jwtToken?.isadmin) {
            navigate("/");
            toast.error("Access denied");
        }
    }, [cookies, navigate]);

    const fetchData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setFetchedData(response.data);
        } catch (error) {}
    };

    const handleDataFetched = (data) => {
        setFetchedData(data);
    };

    const handleItemDeleted = () => {
        fetchData();
    };
    const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

    return (
        <div>
            <NavBar />
            <h2>{pageTitle}</h2>

            <CreateButtonComponent
                data={row}
                columns={columns}
                apiurl={apiUrl}
                id={row.id}
                onSave={handleDataFetched}
                onEditClick={() => setIsModalVisible(true)}
            />
            <DataFetchingComponent
                apiUrl={apiUrl}
                onDataFetched={handleDataFetched}
            />
            <DataTableComponent
                data={fetchedData}
                apiUrl={apiUrl}
                columns={columns}
                onDataFetched={handleItemDeleted}
            />
        </div>
    );
};

export default GenericPage;
