import React, { Component } from "react";
import axios from "axios";

class AdvertisementList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            advertisements: [],
            loading: true,
            error: null,
        };
    }

    componentDidMount() {
        // Fetch advertisements from the API endpoint
        axios
            .get("http://localhost:8080/advertisement")
            .then((response) => {
                this.setState({
                    advertisements: response.data,
                    loading: false,
                    error: null,
                });
            })
            .catch((error) => {
                this.setState({
                    advertisements: [],
                    loading: false,
                    error: error,
                });
            });
    }

    render() {
        const { advertisements, loading, error } = this.state;

        if (loading) {
            return <div>Loading...</div>;
        }

        if (error) {
            return <div>Error occurred: {error.message}</div>;
        }

        return (
            <div>
                <h1>Advertisements</h1>
                <ul>
                    {advertisements.map((advertisement) => (
                        <li key={advertisement.id}>
                            {advertisement.title} - {advertisement.description}
                        </li>
                    ))}
                </ul>
            </div>
        );
    }
}

export default AdvertisementList;
