import { useState, useEffect } from "react";
import axios from "axios";

const useAdditionalInfo = (advertisements) => {
    const [additionalInfo, setAdditionalInfo] = useState({});

    useEffect(() => {
        const fetchAdditionalInfo = async (id) => {
            try {
                const response = await axios.get(
                    `http://localhost:8080/advertisement/${id}`
                );
                setAdditionalInfo((prevInfo) => ({
                    ...prevInfo,
                    [id]: response.data,
                }));
            } catch (error) {
                console.error(
                    `Error fetching additional info for advertisement ${id}:`,
                    error
                );
            }
        };

        advertisements.forEach((advertisement) => {
            fetchAdditionalInfo(advertisement.id);
        });
    }, [advertisements]);

    return additionalInfo;
};

export default useAdditionalInfo;
