import React from "react";

import GenericPage from "../components/GenericPage";

import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import jwtDecode from "jwt-decode";

import NavBar from "../components/NavBar";
import TableHeaderComponent from "../components/TableHeader";
import TableInfoComponent from "../components/TableInfo";
// import EditButton from "../components/EditButton";

// import CreateButton from "../components/CreateButton";

const AdvertisementMonitor = () => {
    const keysToDisplay = ["id", "title", "location", "wage"];
    const displayedHeaders = {
        title: "Job",
        location: "Location",
        wage: "Wage",
    };

    useEffect(() => {
        const jwtToken =
            cookies.jwt !== "undefined" && cookies.jwt !== undefined
                ? jwtDecode(cookies.jwt)
                : undefined;

        if (!jwtToken?.isadmin) {
            navigate("/");
        }
    }, [cookies, navigate]);

    return (
        <div>
            <div>
                <NavBar />
            </div>
            <div>
                <GenericPage
                    pageName="Advertisement Table"
                    tableHeaderComponent={
                        <TableHeaderComponent
                            url="http://localhost:8080/advertisement/monitor"
                            keysToDisplay={keysToDisplay}
                            displayedHeaders={displayedHeaders}
                        />
                    }
                    tableInfoComponent={
                        <TableInfoComponent
                            url="http://localhost:8080/advertisement/monitor"
                            keysToDisplay={keysToDisplay}
                        />
                    }

                    // tableInfoComponent={TableInfo}
                    // editButtonComponent={EditButton}
                    // deleteButtonComponent={DeleteButton}
                    // createButtonComponent={CreateButton}
                />
            </div>
        </div>
    );
};

export default AdvertisementMonitor;
