import React, { useState } from "react";
import FormDataForm from "./generalform";
import Table from "./tableComponent/table";

const Companies = () => {
    const [dataUpdated, setDataUpdated] = useState(false);

    const handleDataUpdate = () => {
        setDataUpdated(!dataUpdated);
    };
    return (
        <div>
            <div>
                <Table
                    apiUrl="http://localhost:8080/companies"
                    itemName="Companies"
                    itemKeys={["id", "name", "description", "employees"]}
                    customColumns={[
                        "id",
                        "Company name",
                        "Description",
                        "Number of employees",
                    ]}
                />
            </div>

            <div>
                <FormDataForm
                    apiUrl={"http://localhost:8080/companies"}
                    formFields={[
                        {
                            name: "name",
                            label: "Company name",
                            type: "text",
                        },
                        {
                            name: "description",
                            label: "Description",
                            type: "text",
                        },
                        {
                            name: "employees",
                            label: "Number of employees",
                            type: "number",
                        },
                    ]}
                />
            </div>
        </div>
    );
};

export default Companies;
