import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const EditButtonComponent = ({ data, columns, apiurl, onSave }) => {
    const [editedData, setEditedData] = useState({ ...data }); // Use spread operator to create a copy of the data
    const [isEditing, setIsEditing] = useState(false);

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseInt(value, 10) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            const response = await axios.post(apiurl, editedData);

            if (response.status === 201) {
                toast.success("Item created successfully!");
                setIsEditing(false);
                onSave(response.data); // Pass the newly created item to the parent component
            } else {
                toast.error("Error creating item");
            }
        } catch (error) {
            toast.error("Error saving item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    return (
        <>
            <button onClick={handleEditClick}>Edit</button>
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {columns.map((column) => (
                            <div key={column.key}>
                                <label htmlFor={column.key}>
                                    {column.header}
                                </label>
                                {column.type === "textarea" ? (
                                    <textarea
                                        id={column.key}
                                        name={column.key}
                                        value={editedData[column.key] || ""}
                                        onChange={handleInputChange}
                                    />
                                ) : (
                                    <input
                                        type={column.type}
                                        id={column.key}
                                        name={column.key}
                                        value={editedData[column.key] || ""}
                                        onChange={handleInputChange}
                                    />
                                )}
                            </div>
                        ))}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default EditButtonComponent;
