import React, { useState, useEffect } from "react";
import axios from "axios";

const Advertisements = () => {
    const [advertisements, setAdvertisements] = useState([]);
    const [newAdvertisement, setNewAdvertisement] = useState({
        companyid: "",
        title: "",
        description: "",
        location: "",
        wage: 0,
        contact: "",
    });
    const [showCompanyForm, setShowCompanyForm] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(
                    "http://localhost:8080/advertisement/getAllForMonitor"
                );
                setAdvertisements(response.data);
            } catch (error) {
                console.error("Error fetching advertisements:", error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/advertisement/${id}`);
            setAdvertisements(advertisements.filter((ad) => ad.id !== id));
        } catch (error) {
            console.error("Error deleting advertisement:", error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewAdvertisement({
            ...newAdvertisement,
            [name]: value,
        });
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                "http://localhost:8080/advertisement",
                newAdvertisement
            );
            setAdvertisements([...advertisements, response.data]);
            setNewAdvertisement({
                companyid: "",
                title: "",
                description: "",
                location: "",
                wage: 0,
                contact: "",
            });
            setShowCompanyForm(false);
            fetchData();
        } catch (error) {
            console.error("Error creating advertisement:", error);
        }
    };

    return (
        <div>
            <h2>Advertisements</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Title</th>
                        <th>Location</th>
                        <th>Application date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {advertisements.map((advertisement) => {
                        const postDate = new Date(advertisement.postdate);
                        const formattedDate = `${postDate
                            .getDate()
                            .toString()
                            .padStart(2, "0")}/${(postDate.getMonth() + 1)
                            .toString()
                            .padStart(2, "0")}/${postDate.getFullYear()}`;

                        return (
                            <tr key={advertisement.id}>
                                <td>{advertisement.name}</td>
                                <td>{advertisement.title}</td>
                                <td>{advertisement.location}</td>
                                <td>{formattedDate}</td>
                                <td>
                                    <button
                                        onClick={() =>
                                            handleDelete(advertisement.id)
                                        }
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>

            <h2>Create Advertisement</h2>
            <form onSubmit={handleFormSubmit}>
                <label>
                    Company name:
                    <input
                        type="text"
                        name="companyid"
                        value={newAdvertisement.companyid}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Title:
                    <input
                        type="text"
                        name="title"
                        value={newAdvertisement.title}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Wage:
                    <input
                        type="number"
                        name="wage"
                        value={newAdvertisement.wage}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Contact:
                    <input
                        type="email"
                        name="contact"
                        value={newAdvertisement.contact}
                        onChange={handleInputChange}
                    />
                </label>
                <label>
                    Location:
                    <input
                        type="text"
                        name="location"
                        value={newAdvertisement.location}
                        onChange={handleInputChange}
                    />
                </label>

                <button type="submit">Create Advertisement</button>
            </form>
        </div>
    );
};

export default Advertisements;
