const CreateButtonComponent = ({ apiurl, keyTypes }) => {
    const [editedData, setEditedData] = useState({});
    const [isEditing, setIsEditing] = useState(false);

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseFloat(value) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            // Validate editedData based on keyTypes
            for (const key in editedData) {
                if (typeof editedData[key] !== keyTypes[key]) {
                    toast.error(`Invalid data type for key: ${key}`);
                    return;
                }
            }

            const response = await axios.put(apiurl, editedData);

            if (response.status === 200) {
                toast.success("Item modified successfully!");
                window.location.reload();
                setIsEditing(false);
            } else {
                toast.error("Error modifying item");
            }
        } catch (error) {
            toast.error("Error modifying item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
        setEditedData({});
    };

    return (
        <>
            <button onClick={() => setIsEditing(true)}>Edit</button>
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {Object.keys(keyTypes).map((key) => (
                            <div key={key}>
                                <label htmlFor={key}>{key}</label>
                                {keyTypes[key] === "boolean" ? (
                                    <select
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    >
                                        <option value={true}>True</option>
                                        <option value={false}>False</option>
                                    </select>
                                ) : keyTypes[key] === "number" ? (
                                    <input
                                        type="number"
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    />
                                ) : (
                                    <input
                                        type="text"
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    />
                                )}
                            </div>
                        ))}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default CreateButtonComponent;
