import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "sonner";

const TableHeaderComponent = ({ url, keysToDisplay, displayedHeaders }) => {
    const [tableHeaders, setTableHeaders] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                await axios
                    .get(url)
                    .then((res) => {
                        setAdvertisement(res.data);
                    })
                    .catch((error) => {
                        toast.error("Error while retieving the advertisements");
                        console.log(error);
                    });

                const headers = keysToDisplay.map(
                    (key) => displayedHeaders[key]
                );

                // Set the headers in the state
                setTableHeaders(headers);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, [url, keysToDisplay, displayedHeaders]);

    return (
        <div className="tableHeader">
            {tableHeaders.map((header, index) => (
                <div key={index} className="headerCell">
                    {header}
                </div>
            ))}
        </div>
    );
};

export default TableHeaderComponent;
