import React, { useState, useEffect } from "react";
import useAdditionalInfo from "../advertisements/component";
import axios from "axios";

const fetchAdvertisements = async () => {
    try {
        const response = await axios.get("http://localhost:8080/application");
        return response.data;
    } catch (error) {
        console.error("Error:", error);
        throw error;
    }
};

const Advertisements = () => {
    const [advertisements, setApplications] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchAdvertisements();
                setApplications(data);
            } catch (error) {
                console.error("Error fetching advertisements:", error);
            }
        };

        fetchData();
    }, []);

    const fetchAdditionalInfo = async (id) => {
        try {
            const response = await axios.get(
                `http://localhost:8080/advertisement/${id}`
            );
            setAdditionalInfo((prevInfo) => ({
                ...prevInfo,
                [id]: response.data, // Store additional info with advertisement id as the key
            }));
        } catch (error) {
            console.error(
                `Error fetching additional info for advertisement ${id}:`,
                error
            );
        }
    };

    useEffect(() => {
        // Fetch additional info for each advertisement when advertisements state is updated
        advertisements.forEach((advertisement) => {
            fetchAdditionalInfo(advertisement.id);
        });
    }, [advertisements]);

    return (
        <div>
            <h2>Job applications</h2>
            {advertisements.map((advertisement) => (
                <div key={advertisement.id}>
                    <h3>
                        {advertisement.name}&nbsp;
                        {advertisement.title}&nbsp;
                        {advertisement.location}
                    </h3>
                    {additionalInfo[advertisement.id] && ( // Check if additional info is available
                        <div>
                            <p>
                                Additional Information:{" "}
                                {additionalInfo[advertisement.id]}
                            </p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default Advertisements;
