import React, { useState } from "react";
import GetAllData from "./getanddelete";
import FormDataForm from "./generalform";

const Advertisements = () => {
    const [dataUpdated, setDataUpdated] = useState(false);

    const handleDataUpdate = () => {
        setDataUpdated(!dataUpdated);
    };
    return (
        <div>
            <div>
                <GetAllData
                    apiUrl="http://localhost:8080/advertisement/monitor"
                    itemName="Advertisements"
                    onDataUpdate={handleDataUpdate}
                    itemKeys={[
                        "title",
                        "location",
                        "name",
                        "wage",
                        "contact",
                        "postdate",
                    ]}
                />
            </div>
            <div>
                <FormDataForm
                    apiUrl={"http://localhost:8080/advertisement/"}
                    onDataUpdate={handleDataUpdate}
                    itemKeys={{
                        title: "text",
                        location: "text",
                        name: "text",
                        wage: "integer",
                        contact: "email",
                    }}
                />
            </div>
        </div>
    );
};

export default Advertisements;
