import { useState, useEffect } from "react";
import * as React from "react";
import "./Table.css";
import DeleteButtonComponent from "./DeleteButton";
import EditButtonComponent from "./EditButton";
import CustomTablePagination from "./TablePagination";

import {
    TablePagination,
    tablePaginationClasses as classes,
} from "@mui/base/TablePagination";

const DataTableComponent = ({ data, columns, apiUrl, onDataFetched }) => {
    const [fetchedData, setFetchedData] = useState([]);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);

    const emptyRows =
        page > 0 ? Math.max(0, (1 + page) * rowsPerPage - rows.length) : 0;

    const handleChangePage = (
        event: React.MouseEvent<HTMLButtonElement> | null,
        newPage: number
    ) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (
        event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
    ) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    useEffect(() => {
        setFetchedData(data);
    }, [data]);

    const handleDataFetched = (updatedData) => {
        setFetchedData(updatedData);
        onDataFetched(updatedData);
    };

    return (
        <div>
            <table className="dataTable">
                <thead className="tableHeader">
                    <tr>
                        {columns.map((column, index) => (
                            <th key={index}>{column.header}</th>
                        ))}
                        <th>delete</th>
                        <th>Edit</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, rowIndex) => (
                        <tr className="infoRow" key={rowIndex}>
                            {columns.map((column, columnIndex) => (
                                <td className="infoRow" key={columnIndex}>
                                    {row[column.key]}
                                </td>
                            ))}
                            <td>
                                <DeleteButtonComponent
                                    id={row.id}
                                    apiUrl={apiUrl}
                                    column={columns}
                                    dataFetching={handleDataFetched}
                                />
                            </td>
                            <td>
                                <EditButtonComponent
                                    data={row}
                                    columns={columns}
                                    apiurl={apiUrl}
                                    id={row.id}
                                    onSave={handleDataFetched}
                                    onEditClick={() => setIsModalVisible(true)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
                <tfoot>
                    <tr>
                        <CustomTablePagination
                            rowsPerPageOptions={[
                                5,
                                10,
                                25,
                                { label: "All", value: -1 },
                            ]}
                            colSpan={3}
                            count={fetchedData.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            slotProps={{
                                select: {
                                    "aria-label": "rows per page",
                                },
                                actions: {
                                    showFirstButton: true,
                                    showLastButton: true,
                                },
                            }}
                            onPageChange={handleChangePage}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                        />
                    </tr>
                </tfoot>
            </table>
        </div>
    );
};

export default DataTableComponent;
