import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const inputTypes = {
    number: "number",
    password: "password",
    email: "email",
    text: "text",
    textarea: "textarea",
};

const EditButtonComponent = ({ data, keysToDisplay, url, id, onSave }) => {
    const [editedData, setEditedData] = useState(data);
    const [isEditing, setIsEditing] = useState(false);

    const handleEditClick = () => {
        setEditedData(data);
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseInt(value, 10) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            const response = await axios.put(`${url}/${id}`, editedData);
            setIsEditing(false);
            onSave(editedData);
            if (response.status === 200) {
                toast.success("Item modifying successfully!");
                window.location.reload();
            } else {
                toast.error("Error modifying item");
            }
        } catch (error) {
            toast.error("Error modifying item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    return (
        <>
            <button onClick={handleEditClick}>Edit</button>
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {keysToDisplay
                            .filter(
                                (key) =>
                                    key !== "id" &&
                                    inputTypes.hasOwnProperty(key)
                            )
                            .map((key) => (
                                <div key={key}>
                                    <label htmlFor={key}>{key}</label>
                                    {inputTypes[key] === "textarea" ? (
                                        <textarea
                                            id={key}
                                            name={key}
                                            value={editedData[key]}
                                            onChange={handleInputChange}
                                        />
                                    ) : (
                                        <input
                                            type={inputTypes[key]}
                                            id={key}
                                            name={key}
                                            value={editedData[key]}
                                            onChange={handleInputChange}
                                        />
                                    )}
                                </div>
                            ))}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default EditButtonComponent;
