import React, { useState, useEffect } from "react";
import axios from "axios";

const fetchAdvertisements = async () => {
    try {
        const response = await axios.get(
            "http://localhost:8080/advertisement/getAllForMonitor"
        );
        return response.data;
    } catch (error) {
        console.error("Error:", error);
        throw error;
    }
};

const Advertisements = () => {
    const [advertisements, setApplications] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchAdvertisements();
                setApplications(data);
            } catch (error) {
                console.error("Error fetching advertisements:", error);
            }
        };

        fetchData();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(
                `http://localhost:8080/advertisement/${advertisementsid}`
            );
        } catch (error) {
            console.error("Error deleting advertisement:", error);
        }
    };

    return (
        <div>
            <h2>Advertisements</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Title</th>
                        <th>Location</th>
                        <th>Application date</th>
                        <th>action</th>
                    </tr>
                </thead>
                <tbody>
                    {advertisements.map((advertisement) => {
                        const postDate = new Date(advertisement.postdate);
                        const formattedDate = `${postDate
                            .getDate()
                            .toString()
                            .padStart(2, "0")}/${(postDate.getMonth() + 1)
                            .toString()
                            .padStart(2, "0")}/${postDate.getFullYear()}`;

                        return (
                            <tr key={advertisement.id}>
                                <td>{advertisement.name}</td>
                                <td>{advertisement.title}</td>
                                <td>{advertisement.location}</td>
                                <td>{formattedDate}</td>
                                <td>
                                    <button
                                        onClick={() =>
                                            handleDelete(advertisement.id)
                                        }
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>
    );
};
export default Advertisements;
