import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import jwtDecode from "jwt-decode";
import NavBar from "./NavBar";
import { toast } from "sonner";

const GenericPage = ({ pageTitle, allowedRoles, children }) => {
    const [cookies] = useCookies(["jwt"]);
    const navigate = useNavigate();

    useEffect(() => {
        const jwtToken =
            cookies.jwt !== "undefined" && cookies.jwt !== undefined
                ? jwtDecode(cookies.jwt)
                : undefined;

        // Check if the user has the required role to access the page
        if (!jwtToken?.isadmin || !allowedRoles.includes(jwtToken?.role)) {
            toast.error("Access denied");
            navigate("/");
        }
    }, [cookies, navigate, allowedRoles]);

    return (
        <div>
            <NavBar />
            <h2>{pageTitle}</h2>
            {children}
        </div>
    );
};

export default GenericPage;
