import React, { useState } from "react";
import axios from "axios";
import "./form.css";
import "../../../pages/advertisements/component/advertisement.css";

const FormDataForm = ({ apiUrl, formFields }) => {
    const [formData, setFormData] = useState({});

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(apiUrl, formData);
            window.location.reload();
        } catch (error) {
            console.error("Error submitting form data:", error);
        }
    };

    const handleInputChange = (e, name, type) => {
        let value = e.target.value;

        if (type === "number" || type === "integer") {
            value = parseInt(value, 10);
        } else if (type === "checkbox") {
            value = e.target.checked;
        }

        setFormData({ ...formData, [name]: value });
    };

    return (
        <div className="form-container">
            <form className="form" onSubmit={handleSubmit}>
                {formFields.map((field) => (
                    <div className="form-group" key={field.name}>
                        <label className="input-label" htmlFor={field.name}>
                            {field.label}
                        </label>
                        {field.type === "checkbox" ? (
                            <input
                                className="checkbox"
                                type={field.type}
                                id={field.name}
                                checked={formData[field.name] || false}
                                onChange={(e) =>
                                    handleInputChange(e, field.name, field.type)
                                }
                            />
                        ) : field.type === "date" ? (
                            <input
                                className="input"
                                type={field.type}
                                id={field.name}
                                value={formData[field.name] || ""}
                                onChange={(e) =>
                                    handleInputChange(e, field.name, field.type)
                                }
                            />
                        ) : (
                            <input
                                className="input"
                                type={field.type}
                                id={field.name}
                                value={formData[field.name] || ""}
                                onChange={(e) =>
                                    handleInputChange(e, field.name, field.type)
                                }
                            />
                        )}
                    </div>
                ))}
                <button className="form-submit-btn" type="submit">
                    Submit
                </button>
            </form>
        </div>
    );
};

export default FormDataForm;
