import React, { useState } from "react";
import axios from "axios";
import "../../../component/form/form.css";

const FormDataForm = ({ apiUrl, formFields }) => {
    const [formData, setFormData] = useState({});

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post(apiUrl, formData);
            window.location.reload();
        } catch (error) {
            console.error("Error submitting form data:", error);
        }
    };

    const handleInputChange = (e, name, type) => {
        let value = e.target.value;

        if (type === "number" || type === "integer") {
            value = parseInt(value, 10);
        } else if (type === "checkbox") {
            value = e.target.checked;
        } else if (type === "date") {
            const regex = /^\d{4}-\d{2}-\d{2}$/;
            if (!regex.test(value)) {
                console.error("Invalid date format");
                return;
            }
        }

        setFormData({ ...formData, [name]: value });
    };

    return (
        <form className="page" onSubmit={handleSubmit}>
            {formFields.map((field) => (
                <div className="input-container" key={field.name}>
                    <label className="input-label" htmlFor={field.name}>
                        {field.label}
                    </label>
                    {field.type === "checkbox" ? (
                        <input
                            className="input"
                            type={field.type}
                            id={field.name}
                            checked={formData[field.name] || false}
                            onChange={(e) =>
                                handleInputChange(e, field.name, field.type)
                            }
                        />
                    ) : field.type === "date" ? (
                        <input
                            type={field.type}
                            id={field.name}
                            value={formData[field.name] || ""}
                            onChange={(e) =>
                                handleInputChange(e, field.name, field.type)
                            }
                        />
                    ) : (
                        <input
                            type={field.type}
                            id={field.name}
                            value={formData[field.name] || ""}
                            onChange={(e) =>
                                handleInputChange(e, field.name, field.type)
                            }
                        />
                    )}
                </div>
            ))}
            <button type="submit">Submit</button>
        </form>
    );
};

export default FormDataForm;
