import React from "react";
import DeleteButton from "./deleteButton";
import EditButton from "./editButton";

const TableRow = ({ item, columns, apiUrl, onDataUpdate }) => {
    const [isEditing, setEditing] = useState(false);
    const [editedData, setEditedData] = useState({});

    // Initialize editedData when item or columns change
    useEffect(() => {
        // Map columns to create an empty editedData object with corresponding keys
        const initialEditedData = {};
        columns.forEach((key) => {
            initialEditedData[key] = item[key] || "";
        });
        setEditedData(initialEditedData);
    }, [item, columns]);

    const handleEdit = () => {
        setEditing(true);
    };

    const handleSave = () => {
        axios
            .put(`${apiUrl}/${item.id}`, editedData)
            .then((response) => {
                console.log("Data updated successfully:", response.data);
                setEditing(false);
                onDataUpdate();
            })
            .catch((error) => {
                console.error("Error updating data:", error.message);
            });
    };

    return (
        <tr>
            {columns.map((key, index) => (
                <td key={index}>
                    {typeof item[key] === "boolean"
                        ? item[key]
                            ? "Yes"
                            : "No"
                        : item[key]}
                </td>
            ))}
            <td>
                <DeleteButton
                    apiUrl={apiUrl}
                    id={item.id}
                    buttonText={"Delete"}
                    onDataUpdate={onDataUpdate}
                />
            </td>
        </tr>
    );
};

export default TableRow;
