import React, { useState, useEffect } from "react";
import "./Table.css";
import DeleteButtonComponent from "./DeleteButton";
import EditButtonComponent from "./EditButton";
import Modal from "./Modal";
const DataTableComponent = ({ columns, apiUrl, onDataFetched }) => {
    const [fetchedData, setFetchedData] = useState([]);
    const [editingRowId, setEditingRowId] = useState(null);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [editingData, setEditingData] = useState(null);

    useEffect(() => {
        setFetchedData(data);
    }, [data]);

    const handleDataFetched = (updatedData) => {
        setFetchedData(updatedData);
        onDataFetched(updatedData);
    };

    const handleCancelEdit = () => {
        setEditingRowId(null);
        setIsModalVisible(false);
        setEditingData(null); // Reset editing data when modal is closed
    };

    const handleEditClick = (row) => {
        setEditingRowId(row.id);
        setEditingData(row); // Set the editing data when Edit button is clicked
        setIsModalVisible(true);
    };
    return (
        <div>
            <table className="dataTable">
                <thead className="tableHeader">
                    <tr>
                        {columns.map((column, index) => (
                            <th key={index}>{column.header}</th>
                        ))}
                        <th>delete</th>
                        <th>Edit</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, rowIndex) => (
                        <tr className="infoRow" key={rowIndex}>
                            {columns.map((column, columnIndex) => (
                                <td className="infoRow" key={columnIndex}>
                                    {row[column.key]}
                                </td>
                            ))}
                            <td>
                                <DeleteButtonComponent
                                    id={row.id}
                                    apiUrl={apiUrl}
                                    column={columns}
                                    dataFetching={handleDataFetched}
                                />
                            </td>
                            <td>
                                <button onClick={() => handleEditClick(row)}>
                                    Edit
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {isModalVisible && (
                <Modal onClose={handleCancelEdit}>
                    <EditButtonComponent
                        data={editingData} // Pass the data of the item being edited
                        columns={columns}
                        apiurl={apiUrl}
                        id={editingData.id} // Pass the ID of the item being edited
                        onSave={handleDataFetched} // Function to handle data after saving
                    />
                </Modal>
            )}
        </div>
    );
};

export default DataTableComponent;
