import React, { useState } from "react";

const EditButtonComponent = ({ row, columns, onUpdate, onCancel }) => {
    const [editedData, setEditedData] = useState({ ...row });

    const handleInputChange = (key, value) => {
        setEditedData({ ...editedData, [key]: value });
    };

    const handleSaveClick = () => {
        // Logic to update data via API (PUT request)
        // ...

        // Call onUpdate to notify the parent component
        onUpdate(editedData);
    };

    const handleCancelClick = () => {
        // Call onCancel to close the editing form
        onCancel();
    };

    return (
        <div>
            {columns.map((column, index) => (
                <div key={index}>
                    <label>{column.header}</label>
                    {column.type === "textarea" ? (
                        <textarea
                            value={editedData[column.key]}
                            onChange={(e) =>
                                handleInputChange(column.key, e.target.value)
                            }
                        />
                    ) : (
                        <input
                            type={column.type}
                            value={editedData[column.key]}
                            onChange={(e) =>
                                handleInputChange(column.key, e.target.value)
                            }
                        />
                    )}
                </div>
            ))}
            <button onClick={handleSaveClick}>Save</button>
            <button onClick={handleCancelClick}>Close</button>
        </div>
    );
};

export default EditButtonComponent;
