import React, { useState, useEffect } from "react";
import axios from "axios";
import "./table.css";
import DeleteButtonComponent from "../components/DeleteButton";

const TableInfoComponent = ({ url, keysToDisplay }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(url);
                const newData = response.data.map((item) => {
                    const extractedData = {};
                    keysToDisplay.forEach((key) => {
                        extractedData[key] = item[key];
                    });
                    return extractedData;
                });
                setData(newData);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, [url, keysToDisplay]);

    return (
        <>
            {data.map((row, rowIndex) => (
                <tr key={rowIndex} className="infoRow">
                    {keysToDisplay.map((key, cellIndex) => (
                        <td key={cellIndex}>{row[key]}</td>
                    ))}
                    <td>
                        <DeleteButtonComponent
                            url={`http://localhost:8080/advertisement/monitor`}
                            id={row.id}
                        />
                    </td>
                </tr>
            ))}
        </>
    );
};

export default TableInfoComponent;
