import React, { useEffect, useState, useMemo } from "react";
import axios from "axios"; // Import axios here
import EditableCell from "./editableCells";
import DeleteButton from "./deleteButton";
import EditButton from "./editButton";

const TableRow = ({ item, columns, apiUrl, onDataUpdate }) => {
    // Initialize editedData with the item prop
    const [editedData, setEditedData] = useState(item);

    // Update editedData when the item prop changes
    useEffect(() => {
        setEditedData(item);
    }, [item]);

    const handleEdit = () => {
        setEditedData(item);
    };

    const handleSave = () => {
        axios
            .put(`${apiUrl}/${item.id}`, editedData)
            .then((response) => {
                console.log("Data updated successfully:", response.data);
                setEditedData(response.data); // Update editedData with the response from the server
                setEditing(false);
                onDataUpdate(); // Trigger parent component update
            })
            .catch((error) => {
                console.error("Error updating data:", error.message);
            });
    };

    const handleInputChange = (key, value) => {
        // Update the specific field in editedData when an input value changes
        setEditedData((prevData) => ({
            ...prevData,
            [key]: value,
        }));
    };

    const [isEditing, setEditing] = useState(false);

    return (
        <tr>
            {columns.map((key, index) => (
                <EditableCell
                    key={index}
                    value={editedData[key]} // Pass the editedData to the EditableCell component
                    onChange={(value) => handleInputChange(key, value)} // Handle input changes
                    isEditing={isEditing}
                />
            ))}
            <td>
                {isEditing ? (
                    <>
                        <button onClick={handleSave}>Save</button>
                        <button onClick={() => setEditing(false)}>
                            Cancel
                        </button>
                    </>
                ) : (
                    <EditButton onEdit={handleEdit} buttonText={"Edit"} />
                )}
                <DeleteButton
                    apiUrl={apiUrl}
                    id={item.id}
                    buttonText={"Delete"}
                    onDataUpdate={onDataUpdate}
                />
            </td>
        </tr>
    );
};

export default TableRow;
