import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const EditButtonComponent = ({ data, keysToDisplay, url, id, onSave }) => {
    const [editedData, setEditedData] = useState(data);
    const [isEditing, setIsEditing] = useState(false);

    const handleEditClick = () => {
        setEditedData(data);
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            const response = await axios.put(`${url}/${id}`, editedData);
            setIsEditing(false);
            onSave(editedData);
            if (response.status === 200) {
                toast.success("Item modifying successfully!");
                window.location.reload();
            } else {
                toast.error("Error deleting item");
            }
        } catch (error) {
            // Handle errors (e.g., show an error message)
            toast.error("Error modifying item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    return (
        <>
            <button onClick={handleEditClick}>Edit</button>
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {keysToDisplay
                            .filter((key) => key !== "id")
                            .map((key) => (
                                <div key={key}>
                                    <label htmlFor={key}>{key}</label>
                                    <input
                                        type="text"
                                        id={key}
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    />
                                </div>
                            ))}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default EditButtonComponent;
