import axios from "axios";

const deleteLogic = async ({ apiUrl, id, onDataUpdate }) => {
    try {
        await axios.delete(`${apiUrl}/${id}`);
        window.location.reload();
    } catch (error) {
        console.error(`Error deleting item:`, error);
    }
};

export default deleteLogic;
