import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const CreateButtonComponent = ({
    apiurl,
    keyTypes,
    onClose,
    onDataCreated,
}) => {
    const [editedData, setEditedData] = useState({});

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseFloat(value) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            for (const key in editedData) {
                if (typeof editedData[key] !== keyTypes[key]) {
                    toast.error(`Invalid data type for key: ${key}`);
                    return;
                }
            }

            const response = await axios.post(apiurl, editedData);

            if (response.status === 201) {
                toast.success("Item created successfully!");
                onDataCreated(); // Trigger the parent component to fetch updated data
                onClose(); // Close the modal after saving the item
            } else {
                toast.error("Error creating item");
            }
        } catch (error) {
            toast.error("Error creating item");
        }
    };

    return (
        <Modal>
            <div className="modal-content">
                {Object.keys(keyTypes).map((key) => (
                    <div key={key}>
                        <label htmlFor={key}>{key}</label>
                        {keyTypes[key] === "boolean" ? (
                            <select
                                name={key}
                                value={editedData[key] || ""}
                                onChange={handleInputChange}
                            >
                                <option value={true}>True</option>
                                <option value={false}>False</option>
                            </select>
                        ) : keyTypes[key] === "number" ? (
                            <input
                                type="number"
                                name={key}
                                value={editedData[key] || ""}
                                onChange={handleInputChange}
                            />
                        ) : (
                            <input
                                type="text"
                                name={key}
                                value={editedData[key] || ""}
                                onChange={handleInputChange}
                            />
                        )}
                    </div>
                ))}
                <button type="button" onClick={handleSaveClick}>
                    Save
                </button>
            </div>
        </Modal>
    );
};

export default CreateButtonComponent;
