import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const CreateButtonComponent = ({ columns, apiurl }) => {
    const initialData = Object.fromEntries(
        columns
            .filter((column) => column.key !== "id")
            .map((column) => [column.key, ""])
    );

    const [editedData, setEditedData] = useState(initialData);
    const [isEditing, setIsEditing] = useState(false);

    const handleEditClick = () => {
        setEditedData(initialData); // Set initial data when editing starts
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseInt(value, 10) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            // Remove 'id' key from editedData before sending the POST request
            const { id, ...postData } = editedData;

            const response = await axios.post(apiurl, postData);

            if (response.status === 201) {
                toast.success("Item created successfully!");
                setIsEditing(false);
            } else {
                toast.error("Error creating item");
            }
        } catch (error) {
            toast.error("Error creating item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    return (
        <>
            <button onClick={handleEditClick}>Create</button>
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {columns.map((column) => {
                            // Exclude 'id' field from the form HTML
                            if (column.key !== "id") {
                                return (
                                    <div key={column.key}>
                                        <label htmlFor={column.key}>
                                            {column.header}
                                        </label>
                                        {column.type === "textarea" ? (
                                            <textarea
                                                id={column.key}
                                                name={column.key}
                                                value={
                                                    editedData[column.key] || ""
                                                }
                                                onChange={handleInputChange}
                                            />
                                        ) : (
                                            <input
                                                type={column.type}
                                                id={column.key}
                                                name={column.key}
                                                value={
                                                    editedData[column.key] || ""
                                                }
                                                onChange={handleInputChange}
                                            />
                                        )}
                                    </div>
                                );
                            }
                            return null; // Exclude 'id' field
                        })}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default CreateButtonComponent;
