import React, { useState, useEffect } from "react";
import "./Table.css";
import DeleteButtonComponent from "./DeleteButton";
const DataTableComponent = ({ data, columns, apiUrl, onDataFetched }) => {
    const [fetchedData, setFetchedData] = useState([]);

    useEffect(() => {
        setFetchedData(data);
    }, [data]); // Re-run this effect when the data prop changes

    const handleDataFetched = () => {
        // Call the onDataFetched function to update the data
        onDataFetched(fetchedData);
    };
    return (
        <table className="dataTable">
            <thead className="tableHeader">
                <tr>
                    {columns.map((column, index) => (
                        <th key={index}>{column.header}</th>
                    ))}
                    <th>delete</th>
                </tr>
            </thead>
            <tbody>
                {data.map((row, rowIndex) => (
                    <tr className="infoRow" key={rowIndex}>
                        {columns.map((column, columnIndex) => (
                            <td className="infoRow" key={columnIndex}>
                                {row[column.key]}
                            </td>
                        ))}
                        <td>
                            <DeleteButtonComponent
                                id={row.id}
                                apiUrl={apiUrl}
                                dataFetching={handleDataFetched}
                            />
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default DataTableComponent;
