import React, { useRef } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
// import Cookies from 'universal-cookie';

function Login() {
    const email = useRef();
    const password = useRef();

    // useEffect(() => {
    //     const cookies = new Cookies();
    //     const jwtCookie = cookies.get('jwt');
    //     console.log(jwtCookie);
    //   }, []);

    const handleLogin = () => {
        const emailValue = email.current.value;
        const passwordValue = password.current.value;
        if (emailValue && passwordValue) {
            axios
                .post("http://localhost:8080/user/signIn",
                    {
                        email: emailValue,
                        password: passwordValue
                    },
                    {
                        withCredentials: true
                    })
                .then((response) => {
                    console.log(response);
                })
                .catch((error) => {
                    console.log(error);
                });
        }
    }

    return (
        <div className="login-page">
            <div className="container">
                <h1>Login</h1>
                <div>
                    <span className="input-label">E-mail adress</span>
                    <input className="input" type="text" ref={email} placeholder="Type your email adress" />
                </div>
                <div>
                    <span className="input-label">Password</span>
                    <input className="input" type="text" ref={password} placeholder="Type your password" />
                </div>
                <button className="login-button" onClick={() => handleLogin()}>Login</button>
                <Link to={"/sign-up"}>Sign up</Link>
            </div>
        </div>
    );
}

export default Login;
