import React, { useRef, useEffect, useState } from "react";
import axios from "axios"; // Import axios here
import EditableCell from "./editableCells";
import DeleteButton from "./deleteButton";
import EditButton from "./editButton";

const TableRow = ({ item, columns, apiUrl, onDataUpdate }) => {
    const [isEditing, setEditing] = useState(false);

    const [editedData, setEditedData] = useState({});

    const handleEdit = () => {
        setEditedData(item);
        setEditing(true);
    };

    useEffect(() => {
        if (isEditing) {
            axios
                .put(`${apiUrl}/${item.id}`, { ...editedData })
                .then((response) => {
                    console.log("Data updated successfully:", response.data);
                    setEditing(false);
                    onDataUpdate(); // Update parent component data after successful update
                })
                .catch((error) => {
                    console.error("Error updating data:", error.message);
                });
        }
    }, [isEditing, editedData, apiUrl, item.id, onDataUpdate]);

    return (
        <tr>
            {columns.map((key, index) => (
                <EditableCell
                    key={index}
                    value={item[key]}
                    onChange={() => {}}
                    isEditing={isEditing}
                />
            ))}
            <td>
                {isEditing ? (
                    <>
                        <button onClick={handleSave}>Save</button>
                        <button onClick={() => setEditing(false)}>
                            Cancel
                        </button>
                    </>
                ) : (
                    <EditButton onEdit={handleEdit} buttonText={"Edit"} />
                )}
                <DeleteButton
                    apiUrl={apiUrl}
                    id={item.id}
                    buttonText={"Delete"}
                    onDataUpdate={onDataUpdate}
                />
            </td>
        </tr>
    );
};

export default TableRow;
