import React, { useState, useEffect } from "react";
import axios from "axios";

const fetchAdvertisements = async () => {
    try {
        const response = await axios.get(
            "http://localhost:8080/advertisement/getAllForMonitor"
        );
        return response.data;
    } catch (error) {
        console.error("Error:", error);
        throw error;
    }
};

const Advertisements = () => {
    const [advertisements, setApplications] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await fetchAdvertisements();
                setApplications(data);
            } catch (error) {
                console.error("Error fetching advertisements:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h2>Advertisments</h2>
            {advertisements.map((advertisement) => (
                <div key={advertisement.id}>
                    <h3>
                        {advertisement.name}&nbsp;
                        {advertisement.title}&nbsp;
                        {advertisement.location}
                    </h3>
                    {additionalInfo[advertisement.id] && (
                        <div>
                            <p>
                                Description:{" "}
                                {additionalInfo[advertisement.id].description}
                            </p>
                            <p>Wage: {additionalInfo[advertisement.id].wage}</p>
                            <p>
                                Post Date:{" "}
                                {
                                    additionalInfo[
                                        advertisement.advertisementid
                                    ].postdate
                                }
                            </p>
                            <p>
                                Contact:{" "}
                                {additionalInfo[advertisement.id].contact}
                            </p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default Advertisements;
