import React, { useState, useEffect } from "react";
import "./Table.css";
import DeleteButtonComponent from "./DeleteButton";
import EditButtonComponent from "./EditButton";
import EditModal from "./Modal";
const DataTableComponent = ({ data, columns, apiUrl, onDataFetched }) => {
    const [fetchedData, setFetchedData] = useState([]);
    const [editingRow, setEditingRow] = useState(null);
    const [isModalVisible, setIsModalVisible] = useState(false);

    useEffect(() => {
        setFetchedData(data);
    }, [data]);

    const handleEditClick = (row) => {
        setEditingRow(row);
        setIsModalVisible(true);
    };

    const handleUpdate = (updatedData) => {
        // Logic to update data via API (PUT request)
        // ...

        // Update the state and close the editing form
        setFetchedData((prevData) =>
            prevData.map((row) =>
                row.id === updatedData.id ? { ...row, ...updatedData } : row
            )
        );

        setIsModalVisible(false);
        setEditingRow(null);
    };

    const handleCloseModal = () => {
        setIsModalVisible(false);
        setEditingRow(null);
    };

    useEffect(() => {
        setFetchedData(data);
    }, [data]);

    const handleDataFetched = () => {
        onDataFetched(fetchedData);
    };
    return (
        <div>
            <EditModal
                show={isModalVisible}
                onHide={handleCloseModal}
                row={editingRow}
                columns={columns}
                onUpdate={handleUpdate}
            />
            <table className="dataTable">
                <thead className="tableHeader">
                    <tr>
                        {columns.map((column, index) => (
                            <th key={index}>{column.header}</th>
                        ))}
                        <th>delete</th>
                        <th>Edit</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, rowIndex) => (
                        <tr className="infoRow" key={rowIndex}>
                            {columns.map((column, columnIndex) => (
                                <td className="infoRow" key={columnIndex}>
                                    {row[column.key]}
                                </td>
                            ))}
                            <td>
                                <DeleteButtonComponent
                                    id={row.id}
                                    apiUrl={apiUrl}
                                    dataFetching={handleDataFetched}
                                />
                            </td>
                            <td>
                                {editingRow && editingRow.id === row.id ? (
                                    <EditButtonComponent
                                        row={editingRow}
                                        columns={columns}
                                        onUpdate={handleUpdate}
                                        onCancel={handleCancelEdit}
                                    />
                                ) : (
                                    <button
                                        onClick={() => handleEditClick(row)}
                                    >
                                        Edit
                                    </button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default DataTableComponent;
