import React, { useState } from "react";
import axios from "axios";
import Modal from "./Modal";
import { toast } from "sonner";

const CreateButtonComponent = ({ data, apiurl, id }) => {
    const [editedData, setEditedData] = useState(data);
    const [isEditing, setIsEditing] = useState(false);

    const handleEditClick = () => {
        setEditedData(data);
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;
        setEditedData((prevData) => ({
            ...prevData,
            [name]: type === "number" ? parseFloat(value) : value,
        }));
    };

    const handleSaveClick = async () => {
        try {
            const response = await axios.put(`${apiurl}/${id}`, editedData);

            if (response.status === 200) {
                toast.success("Item modified successfully!");
                window.location.reload();
                setIsEditing(false);
            } else {
                toast.error("Error modifying item");
            }
        } catch (error) {
            toast.error("Error modifying item");
        }
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    return (
        <>
            <button onClick={handleEditClick}>Edit</button>
            {isEditing && (
                <Modal onClose={handleCancelClick}>
                    <form>
                        {Object.keys(editedData).map((key) => (
                            <div key={key}>
                                <label htmlFor={key}>{key}</label>
                                {typeof editedData[key] === "boolean" ? (
                                    <select
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    >
                                        <option value={true}>True</option>
                                        <option value={false}>False</option>
                                    </select>
                                ) : typeof editedData[key] === "number" ? (
                                    <input
                                        type="number"
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    />
                                ) : (
                                    <input
                                        type="text"
                                        name={key}
                                        value={editedData[key]}
                                        onChange={handleInputChange}
                                    />
                                )}
                            </div>
                        ))}
                        <button type="button" onClick={handleSaveClick}>
                            Save
                        </button>
                        <button type="button" onClick={handleCancelClick}>
                            Cancel
                        </button>
                    </form>
                </Modal>
            )}
        </>
    );
};

export default CreateButtonComponent;
